//github.com/coax/simple-tooltips 0.1
$.fn.tooltip=function(){$(this).on({mouseenter:function(e){if(c=$(this).attr("title"),tt=$('<div class="tooltip"></div>'),!c||""==c)return!1;$(this).removeAttr("title"),tt.css("display","none").html(c).appendTo("body");var o=e.pageX,i=e.pageY,n=tt.outerWidth(),s=tt.outerHeight(),p=o-n/2;t=i-s-15,p+n>$(window).width()&&(p=o-n),0>p&&(p=o),i-s-15<$(window).scrollTop()&&(t=i+15),xx=p-o,yy=t-i,tt.css({top:t+"px",left:p+"px"}).fadeIn(100)},mousemove:function(t){tt.css({top:t.pageY+yy+"px",left:t.pageX+xx+"px"})},mouseleave:function(t){$(this).attr("title",c),tt.stop().fadeOut(50,function(){$(this).remove()}),tt=""}})};
